    aws_dms_endpoint: {"attrs":[{"name":"endpoint_arn","description":"The Amazon Resource Name (ARN) for the endpoint."}],"args":[{"name":"certificate_arn","description":"(Optional, Default: empty string) The Amazon Resource Name (ARN) for the certificate."},{"name":"database_name","description":"(Optional) The name of the endpoint database."},{"name":"endpoint_id","description":"(Required) The database endpoint identifier.\n\nMust contain from 1 to 255 alphanumeric characters or hyphens.\nMust begin with a letter\nMust contain only ASCII letters, digits, and hyphens\nMust not end with a hyphen\nMust not contain two consecutive hyphens"},{"name":"endpoint_type","description":"(Required) The type of endpoint. Can be one of source | target.\n"},{"name":"engine_name","description":"(Required) The type of engine for the endpoint. Can be one of mysql | oracle | postgres | mariadb | aurora | redshift | sybase | sqlserver | dynamodb.\n"},{"name":"extra_connection_attributes","description":"(Optional) Additional attributes associated with the connection. For available attributes see Using Extra Connection Attributes with AWS Database Migration Service.\n"},{"name":"kms_key_arn","description":"(Optional) The Amazon Resource Name (ARN) for the KMS key that will be used to encrypt the connection parameters. If you do not specify a value for kms_key_arn, then AWS DMS will use your default encryption key. AWS KMS creates the default encryption key for your AWS account. Your AWS account has a different default encryption key for each AWS region.\n"},{"name":"password","description":"(Optional) The password to be used to login to the endpoint database.\n"},{"name":"port","description":"(Optional) The port used by the endpoint database.\n"},{"name":"server_name","description":"(Optional) The host name of the server.\n"},{"name":"ssl_mode","description":"(Optional, Default: none) The SSL mode to use for the connection. Can be one of none | require | verify-ca | verify-full\n"},{"name":"tags","description":"(Optional) A mapping of tags to assign to the resource.\n"},{"name":"username","description":"(Optional) The user name to be used to login to the endpoint database.\n"},{"name":"service_access_role","description":"ptional) The Amazon Resource Name (ARN) used by the service access IAM role for dynamodb endpoints.\n"}]},
    aws_dms_replication_instance: {"attrs":[{"name":"replication_instance_arn","description":"The Amazon Resource Name (ARN) of the replication instance."},{"name":"replication_instance_private_ips","description":"A list of the private IP addresses of the replication instance."},{"name":"replication_instance_public_ips","description":"A list of the public IP addresses of the replication instance."}],"args":[{"name":"allocated_storage","description":"(Optional, Default: 50, Min: 5, Max: 6144) The amount of storage (in gigabytes) to be initially allocated for the replication instance."},{"name":"apply_immediately","description":"(Optional, Default: false) Indicates whether the changes should be applied immediately or during the next maintenance window. Only used when updating an existing resource."},{"name":"auto_minor_version_upgrade","description":"(Optional, Default: false) Indicates that minor engine upgrades will be applied automatically to the replication instance during the maintenance window."},{"name":"availability_zone","description":"(Optional) The EC2 Availability Zone that the replication instance will be created in."},{"name":"engine_version","description":"(Optional) The engine version number of the replication instance."},{"name":"kms_key_arn","description":"(Optional) The Amazon Resource Name (ARN) for the KMS key that will be used to encrypt the connection parameters. If you do not specify a value for kms_key_arn, then AWS DMS will use your default encryption key. AWS KMS creates the default encryption key for your AWS account. Your AWS account has a different default encryption key for each AWS region."},{"name":"multi_az","description":"(Optional) Specifies if the replication instance is a multi-az deployment. You cannot set the availability_zone parameter if the multi_az parameter is set to true."},{"name":"preferred_maintenance_window","description":"(Optional) The weekly time range during which system maintenance can occur, in Universal Coordinated Time (UTC).\n\nDefault: A 30-minute window selected at random from an 8-hour block of time per region, occurring on a random day of the week.\nFormat: ddd:hh24:mi-ddd:hh24:mi\nValid Days: mon, tue, wed, thu, fri, sat, sun\nConstraints: Minimum 30-minute window."},{"name":"publicly_accessible","description":"(Optional, Default: false) Specifies the accessibility options for the replication instance. A value of true represents an instance with a public IP address. A value of false represents an instance with a private IP address.\n"},{"name":"replication_instance_class","description":"(Required) The compute and memory capacity of the replication instance as specified by the replication instance class. Can be one of dms.t2.micro | dms.t2.small | dms.t2.medium | dms.t2.large | dms.c4.large | dms.c4.xlarge | dms.c4.2xlarge | dms.c4.4xlarge\n"},{"name":"replication_instance_id","description":"(Required) The replication instance identifier. This parameter is stored as a lowercase string.\n\nMust contain from 1 to 63 alphanumeric characters or hyphens.\nFirst character must be a letter.\nCannot end with a hyphen\nCannot contain two consecutive hyphens."},{"name":"replication_subnet_group_id","description":"(Optional) A subnet group to associate with the replication instance.\n"},{"name":"tags","description":"(Optional) A mapping of tags to assign to the resource.\n"},{"name":"vpc_security_group_ids","description":"(Optional) A list of VPC security group IDs to be used with the replication instance. The VPC security groups must work with the VPC containing the replication instance.\n"}]},
    aws_dms_replication_subnet_group: {"attrs":[{"name":"vpc_id","description":"The ID of the VPC the subnet group is in."}],"args":[{"name":"replication_subnet_group_description","description":"(Required) The description for the subnet group."},{"name":"replication_subnet_group_id","description":"(Required) The name for the replication subnet group. This value is stored as a lowercase string.\n\nMust contain no more than 255 alphanumeric characters, periods, spaces, underscores, or hyphens.\nMust not be \"default\"."},{"name":"subnet_ids","description":"(Required) A list of the EC2 subnet IDs for the subnet group.\n"}]},
