    aws_dynamodb_table: {"attrs":[{"name":"arn","description":"The arn of the table"},{"name":"id","description":"The name of the table"},{"name":"stream_arn","description":"The ARN of the Table Stream. Only available when stream_enabled = true"},{"name":"stream_label","description":"A timestamp, in ISO 8601 format, for this stream. Note that this timestamp is not a unique identifier for the stream on its own. However, the combination of AWS customer ID, table name and this field is guaranteed to be unique. It can be used for creating CloudWatch Alarms. Only available when stream_enabled = true"}],"args":[{"name":"name","description":"(Required) The name of the table, this needs to be unique within a region."},{"name":"read_capacity","description":"(Required) The number of read units for this table"},{"name":"write_capacity","description":"(Required) The number of write units for this table"},{"name":"hash_key","description":"(Required, Forces new resource) The attribute to use as the hash key (the attribute must also be defined as an attribute record"},{"name":"range_key","description":"(Optional, Forces new resource) The attribute to use as the range key (must also be defined)"},{"name":"attribute","description":"Define an attribute, has two properties:\nname - The name of the attribute\ntype - One of: S, N, or B for (S)tring, (N)umber or (B)inary data"},{"name":"stream_enabled","description":"(Optional) Indicates whether Streams are to be enabled (true) or disabled (false)."},{"name":"stream_view_type","description":"(Optional) When an item in the table is modified, StreamViewType determines what information is written to the table's stream. Valid values are KEYS_ONLY, NEW_IMAGE, OLD_IMAGE, NEW_AND_OLD_IMAGES."},{"name":"ttl","description":"(Optional) Defines ttl, has two properties, and can only be specified once:\nenabled - (Required) Indicates whether ttl is enabled (true) or disabled (false).\nattribute_name - (Required) The name of the table attribute to store the TTL timestamp in."},{"name":"local_secondary_index","description":"(Optional, Forces new resource) Describe an LSI on the table; these can only be allocated at creation so you cannot change this definition after you have created the resource."},{"name":"global_secondary_index","description":"(Optional) Describe a GSO for the table; subject to the normal limits on the number of GSIs, projected attributes, etc."},{"name":"tags","description":"(Optional) A map of tags to populate on the created table."}]},
    aws_alb: {"attrs":[{"name":"id","description":"The ARN of the load balancer (matches arn)."},{"name":"arn","description":"The ARN of the load balancer (matches id)."},{"name":"arn_suffix","description":"The ARN suffix for use with CloudWatch Metrics."},{"name":"dns_name","description":"The DNS name of the load balancer."},{"name":"canonical_hosted_zone_id","description":"The canonical hosted zone ID of the load balancer."},{"name":"zone_id","description":"The canonical hosted zone ID of the load balancer (to be used in a Route 53 Alias record)."}],"args":[{"name":"name","description":"(Optional) The name of the ALB. This name must be unique within your AWS account, can have a maximum of 32 characters, must contain only alphanumeric characters or hyphens, and must not begin or end with a hyphen. If not specified, Terraform will autogenerate a name beginning with tf-lb."},{"name":"name_prefix","description":"(Optional) Creates a unique name beginning with the specified prefix. Conflicts with name."},{"name":"internal","description":"(Optional) If true, the ALB will be internal."},{"name":"security_groups","description":"(Optional) A list of security group IDs to assign to the ALB."},{"name":"access_logs","description":"(Optional) An Access Logs block. Access Logs documented below."},{"name":"subnets","description":"(Required) A list of subnet IDs to attach to the ALB."},{"name":"idle_timeout","description":"(Optional) The time in seconds that the connection is allowed to be idle. Default: 60."},{"name":"enable_deletion_protection","description":"(Optional) If true, deletion of the load balancer will be disabled via the AWS API. This will prevent Terraform from deleting the load balancer. Defaults to false."},{"name":"ip_address_type","description":"(Optional) The type of IP addresses used by the subnets for your load balancer. The possible values are ipv4 and dualstack"},{"name":"tags","description":"(Optional) A mapping of tags to assign to the resource."}]},
