    aws_ami: {"attrs":[{"name":"id","description":"The ID of the created AMI."}],"args":[{"name":"name","description":"(Required) A region-unique name for the AMI."},{"name":"description","description":"(Optional) A longer, human-readable description for the AMI."},{"name":"virtualization_type","description":"(Optional) Keyword to choose what virtualization mode created instances will use. Can be either \"paravirtual\" (the default) or \"hvm\". The choice of virtualization type changes the set of further arguments that are required, as described below."},{"name":"architecture","description":"(Optional) Machine architecture for created instances. Defaults to \"x86_64\"."},{"name":"ebs_block_device","description":"(Optional) Nested block describing an EBS block device that should be attached to created instances. The structure of this block is described below."},{"name":"ephemeral_block_device","description":"(Optional) Nested block describing an ephemeral block device that should be attached to created instances. The structure of this block is described below."}]},
    aws_ami_copy: {"attrs":[{"name":"id","description":"The ID of the created AMI."}],"args":[{"name":"name","description":"(Required) A region-unique name for the AMI."},{"name":"source_ami_id","description":"(Required) The id of the AMI to copy. This id must be valid in the region given by source_ami_region."},{"name":"source_ami_region","description":"(Required) The region from which the AMI will be copied. This may be the same as the AWS provider region in order to create a copy within the same region."},{"name":"encrypted","description":"(Optional) Specifies whether the destination snapshots of the copied image should be encrypted. Defaults to false"},{"name":"kms_key_id","description":"(Optional) The full ARN of the KMS Key to use when encrypting the snapshots of an image during a copy operation. If not specified, then the default AWS KMS Key will be used"}]},
