    aws_app_cookie_stickiness_policy: {"attrs":[{"name":"id","description":"The ID of the policy."},{"name":"name","description":"The name of the stickiness policy."},{"name":"load_balancer","description":"The name of load balancer to which the policy is attached."},{"name":"lb_port","description":"The load balancer port to which the policy is applied."},{"name":"cookie_name","description":"The application cookie whose lifetime the ELB's cookie should follow."}],"args":[{"name":"name","description":"(Required) The name of the stickiness policy."},{"name":"load_balancer","description":"(Required) The name of load balancer to which the policy should be attached."},{"name":"lb_port","description":"(Required) The load balancer port to which the policy should be applied. This must be an active listener on the load balancer."},{"name":"cookie_name","description":"(Required) The application cookie whose lifetime the ELB's cookie should follow."}]},
