    aws_launch_configuration: {"attrs":[{"name":"id","description":"The ID of the launch configuration."},{"name":"name","description":"The name of the launch configuration."}],"args":[{"name":"name","description":"(Optional) The name of the launch configuration. If you leave this blank, Terraform will auto-generate a unique name."},{"name":"name_prefix","description":"(Optional) Creates a unique name beginning with the specified prefix. Conflicts with name."},{"name":"image_id","description":"(Required) The EC2 image ID to launch."},{"name":"instance_type","description":"(Required) The size of instance to launch."},{"name":"iam_instance_profile","description":"(Optional) The IAM instance profile to associate with launched instances."},{"name":"key_name","description":"(Optional) The key name that should be used for the instance."},{"name":"security_groups","description":"(Optional) A list of associated security group IDS."},{"name":"associate_public_ip_address","description":"(Optional) Associate a public ip address with an instance in a VPC."},{"name":"vpc_classic_link_id","description":"(Optional) The ID of a ClassicLink-enabled VPC. Only applies to EC2-Classic instances. (eg. vpc-2730681a)"},{"name":"vpc_classic_link_security_groups","description":"(Optional) The IDs of one or more security groups for the specified ClassicLink-enabled VPC (eg. sg-46ae3d11)."},{"name":"user_data","description":"(Optional) The user data to provide when launching the instance."},{"name":"enable_monitoring","description":"(Optional) Enables/disables detailed monitoring. This is enabled by default."},{"name":"ebs_optimized","description":"(Optional) If true, the launched EC2 instance will be EBS-optimized."},{"name":"root_block_device","description":"(Optional) Customize details about the root block device of the instance. See Block Devices below for details."},{"name":"ebs_block_device","description":"(Optional) Additional EBS block devices to attach to the instance. See Block Devices below for details."},{"name":"ephemeral_block_device","description":"(Optional) Customize Ephemeral (also known as \"Instance Store\") volumes on the instance. See Block Devices below for details."},{"name":"spot_price","description":"(Optional) The price to use for reserving spot instances."},{"name":"placement_tenancy","description":"(Optional) The tenancy of the instance. Valid values are \"default\" or \"dedicated\", see AWS's Create Launch Configuration for more details"}]},
    aws_lb_cookie_stickiness_policy: {"attrs":[{"name":"id","description":"The ID of the policy."},{"name":"name","description":"The name of the stickiness policy."},{"name":"load_balancer","description":"The load balancer to which the policy is attached."},{"name":"lb_port","description":"The load balancer port to which the policy is applied."},{"name":"cookie_expiration_period","description":"The time period after which the session cookie is considered stale, expressed in seconds."}],"args":[{"name":"name","description":"(Required) The name of the stickiness policy."},{"name":"load_balancer","description":"(Required) The load balancer to which the policy should be attached."},{"name":"lb_port","description":"(Required) The load balancer port to which the policy should be applied. This must be an active listener on the load balancer."},{"name":"cookie_expiration_period","description":"(Optional) The time period after which the session cookie should be considered stale, expressed in seconds."}]},
    aws_lb_ssl_negotiation_policy: {"attrs":[{"name":"id","description":"The ID of the policy."},{"name":"name","description":"The name of the stickiness policy."},{"name":"load_balancer","description":"The load balancer to which the policy is attached."},{"name":"lb_port","description":"The load balancer port to which the policy is applied."},{"name":"attribute","description":"The SSL Negotiation policy attributes."}],"args":[{"name":"name","description":"(Required) The name of the SSL negotiation policy."},{"name":"load_balancer","description":"(Required) The load balancer to which the policy should be attached."},{"name":"lb_port","description":"(Required) The load balancer port to which the policy should be applied. This must be an active listener on the load balancer."},{"name":"attribute","description":"(Optional) An SSL Negotiation policy attribute. Each has two properties:\nname - The name of the attribute\nvalue - The value of the attribute\n"}]},
