    aws_placement_group: {"attrs":[{"name":"id","description":"The name of the placement group."}],"args":[{"name":"name","description":"(Required) The name of the placement group."},{"name":"strategy","description":"(Required) The placement strategy. The only supported value is cluster"}]},
