    aws_spot_datafeed_subscription: {"attrs":[],"args":[{"name":"bucket","description":"(Required) The Amazon S3 bucket in which to store the Spot instance data feed."},{"name":"prefix","description":"(Optional) Path of folder inside bucket to place spot pricing data."}]},
    aws_spot_fleet_request: {"attrs":[{"name":"id","description":"The Spot fleet request ID"},{"name":"spot_request_state","description":"The state of the Spot fleet request."}],"args":[{"name":"iam_fleet_role","description":"(Required) Grants the Spot fleet permission to terminate Spot instances on your behalf when you cancel its Spot fleet request using CancelSpotFleetRequests or when the Spot fleet request expires, if you set terminateInstancesWithExpiration."},{"name":"replace_unhealthy_instances","description":"(Optional) Indicates whether Spot fleet should replace unhealthy instances. Default false."},{"name":"launch_specification","description":"Used to define the launch configuration of the spot-fleet request. Can be specified multiple times to define different bids across different markets and instance types.\n\nNote: This takes in similar but not identical inputs as aws_instance. There are limitations on what you can specify (tags, for example, are not supported). See the list of officially supported inputs in the reference documentation. Any normal aws_instance parameter that corresponds to those inputs may be used.\n\n"},{"name":"spot_price","description":"(Required) The bid price per unit hour.\n\n"},{"name":"wait_for_fulfillment","description":"(Optional; Default: false) If set, Terraform will wait for the Spot Request to be fulfilled, and will throw an error if the timeout of 10m is reached.\n\n"},{"name":"target_capacity","description":"The number of units to request. You can choose to set the target capacity in terms of instances or a performance characteristic that is important to your application workload, such as vCPUs, memory, or I/O.\n\n"},{"name":"allocation_strategy","description":"Indicates how to allocate the target capacity across the Spot pools specified by the Spot fleet request. The default is lowestPrice.\n\n"},{"name":"excess_capacity_termination_policy","description":"Indicates whether running Spot instances should be terminated if the target capacity of the Spot fleet request is decreased below the current size of the Spot fleet.\n\n"},{"name":"terminate_instances_with_expiration","description":"Indicates whether running Spot instances should be terminated when the Spot fleet request expires.\n\n"},{"name":"valid_until","description":"The end date and time of the request, in UTC ISO8601 format (for example, YYYY-MM-DDTHH:MM:SSZ). At this point, no new Spot instance requests are placed or enabled to fulfill the request. Defaults to 24 hours.\n\n"}]},
    aws_spot_instance_request: {"attrs":[{"name":"id","description":"The Spot Instance Request ID."}],"args":[{"name":"spot_price","description":"(Required) The price to request on the spot market."},{"name":"wait_for_fulfillment","description":"(Optional; Default: false) If set, Terraform will wait for the Spot Request to be fulfilled, and will throw an error if the timeout of 10m is reached."},{"name":"spot_type","description":"(Optional; Default: \"persistent\") If set to \"one-time\", after the instance is terminated, the spot request will be closed. Also, Terraform can't manage one-time spot requests, just launch them."},{"name":"launch_group","description":"(Optional) A launch group is a group of spot instances that launch together and terminate together. If left empty instances are launched and terminated individually."},{"name":"block_duration_minutes","description":"(Optional) The required duration for the Spot instances, in minutes. This value must be a multiple of 60 (60, 120, 180, 240, 300, or 360). The duration period starts as soon as your Spot instance receives its instance ID. At the end of the duration period, Amazon EC2 marks the Spot instance for termination and provides a Spot instance termination notice, which gives the instance a two-minute warning before it terminates. Note that you can't specify an Availability Zone group or a launch group if you specify a duration."}]},
    aws_volume_attachment: {"attrs":[{"name":"device_name","description":"The device name exposed to the instance"},{"name":"instance_id","description":"ID of the Instance"},{"name":"volume_id","description":"ID of the Volume"}],"args":[{"name":"device_name","description":"(Required) The device name to expose to the instance (for example, /dev/sdh or xvdh)"},{"name":"instance_id","description":"(Required) ID of the Instance to attach to"},{"name":"volume_id","description":"(Required) ID of the Volume to be attached"},{"name":"force_detach","description":"(Optional, Boolean) Set to true if you want to force the volume to detach. Useful if previous attempts failed, but use this option only as a last resort, as this can result in data loss. See Detaching an Amazon EBS Volume from an Instance for more information."},{"name":"skip_destroy","description":"(Optional, Boolean) Set this to true if you do not wish to detach the volume from the instance to which it is attached at destroy time, and instead just remove the attachment from Terraform state. This is useful when destroying an instance which has volumes created by some other means attached."}]},
