    aws_waf_rate_based_rule: {"attrs":[{"name":"id","description":"The ID of the WAF rule."}],"args":[{"name":"metric_name","description":"(Required) The name or description for the Amazon CloudWatch metric of this rule."},{"name":"name","description":"(Required) The name or description of the rule."},{"name":"rate_key","description":"(Required) Valid value is IP."},{"name":"rate_limit","description":"(Required) The maximum number of requests, which have an identical value in the field specified by the RateKey, allowed in a five-minute period. Minimum value is 2000."},{"name":"predicates","description":"(Optional) One of ByteMatchSet, IPSet, SizeConstraintSet, SqlInjectionMatchSet, or XssMatchSet objects to include in a rule."}]},
